import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProduitsForClient } from '../redux/actions/actions'

function NavBar() {
    return (
        <nav className="navbar navbar-light bg-primary">
            <div className="container-fluid">
                <a className="navbar-brand text-light mx-4">Navbar</a>
                <form className="d-flex mx-4">
                    <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-light" type="submit">Search</button>
                </form>
            </div>
        </nav>
    )
}

function CardsProduits() {
    const dispatch= useDispatch()
    const selectors=useSelector(state=>state.getProduct)

    useEffect(async ()=> {
        await dispatch(getProduitsForClient())
    },[])

    const datas = selectors.map((data, key) =>{
        if(data){

            <div key={key} className="card">
        <div className="card-body card-body-img">
            <img src={data.picture} className="pr_img" />
        </div>
        <div className="card-footer footer-img">
            <h6>Prix : {data.prix}</h6>

        </div>

        <button className="btn btn-primary" type="button" data-bs-toggle="offcanvas" data-bs-target="#offcanvasTop" aria-controls="offcanvasTop" >Modifier le Prix</button>

        <div className="offcanvas offcanvas-top" tabindex="-1" id="offcanvasTop" aria-labelledby="offcanvasTopLabel">
            <div className="offcanvas-header">
                <h5 id="offcanvasTopLabel">Modifier le Prix</h5>
                <button type="button" className="btn-close text-reset" data-bs-dismiss="offcanvas" aria-label="Close"></button>
            </div>
            <div className="offcanvas-body">
                <form >
                    <input type="number" className="form-control"  /><br></br>
                    <button type="submit" className="btn btn-primary form-control">Modifier</button>
                </form>
            </div>
        </div>

    </div>
}}
)
return(
        <div>
            hello world
        </div>
    )
}

export default function Produits() {
    return (
        <div>
            <NavBar/>
            <CardsProduits/>
        </div>
    )
}
