import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProduitsForClient } from '../redux/actions/actions'

function NavBar() {
    return (
        <nav className="navbar navbar-light bg-primary">
            <div className="container-fluid">
                <a className="navbar-brand text-light mx-4">Barber-shop</a>
                <form className="d-flex mx-4">
                    <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-light" type="submit">Search</button>
                </form>
            </div>
        </nav>
    )
}



function CardsProduits() {
    const [key, setKey] = useState(0)
    const state = useSelector(state => state.getProduct)
    if (state[key] !== undefined)
        console.log(state[key])

        const setIdcookie=(id) =>{
            axios({
                method:"post",
                withCredentials:true,
                url:"http://localhost:9000/api/set/produit",
                data: {
                    id:id,
                }
            }).then((user) => {
                return user
            })
            .catch((err) => {
                console.log(err)
            })
        }
         
    const card = state.map((user, key) => {
        if (user) {
            return (
                <div key={key} className="card">
                    <div className="card-header">
                        <h5 className="card-title bg-primary number text-light">{key}</h5>
                    </div>
                    <div className="card-body card-body-img">
                        <img src={user.picture} className="pr_img" />

                    </div>
                    <div className="card-footer footer-img">
                        <h6>Prix : {user.prix}</h6>
                        <button onClick={() => setKey(key)} className="" data-bs-toggle="offcanvas" data-bs-target="#offcanvasRight" aria-controls="offcanvasRight">
                            <i className="fas fa-cart-plus"></i>
                        </button>
                    </div>
                    <div class="offcanvas offcanvas-end" tabindex="-1" id="offcanvasRight" aria-labelledby="offcanvasRightLabel">
                    <div class="offcanvas-header">
                        <h5 id="offcanvasRightLabel">Envoyer le RV</h5>
                        <button type="button" class="btn-close text-reset" data-bs-dismiss="offcanvas" aria-label="Close"></button>
                    </div>

                    <div class="offcanvas-body ">
                        <div class="card card-body">
                            <img src={state[key]} className="pr_img_rv" />
                            <div>
                                <h5>Prix : {key}</h5>
                            </div>
                            <div className="flex-simple">
                                <h5>Heure:</h5>
                                <select className="form-select" aria-label="Default select example">
                                    <option>10:00</option>
                                    <option>11:00</option>
                                    <option>12:00</option>
                                    <option>13:00</option>
                                    <option>14:00</option>
                                    <option>15:00</option>
                                    <option>16:00</option>
                                    <option>17:00</option>
                                    <option>18:00</option>
                                    <option>19:00</option>
                                    <option>20:00</option>
                                    <option>21:00</option>
                                </select>
                            </div></div>
                    </div>
                    <div className="offcanvas-footer center mb-4">
                        <button className="edit"> <i className="fas fa-paper-plane"></i> Envoyer</button>
                    </div>
                </div>
                </div>
            )
        }
    })


    return (
        <div className="flex-card">
            {card}
           
        </div>
    )
}

export default function Produits() {
    const dispatch = useDispatch()
    useEffect(async () => {
        await dispatch(getProduitsForClient())
    }, [])
    return (
        <div>
            <NavBar />
            <CardsProduits />
        </div>
    )
}
