import axios from "axios"

export const GET_DATA ="get/data/user"

export const getData=(id)=>{
  return async (dispatch)=>{
    await axios({
      method: "get",
      withCredentials:true,
      url: "http://localhost:9000/api/"+id,
    }).then(user =>{
      dispatch({type: GET_DATA, payload:user.data})
    })
  }
}
